mapper.select:
SELECT COUNT(*)
FROM CARTS
WHERE ITEM_CODE = #{itemCode} AND USER_CODE = #{userCode} AND USER_ID = #{userId}

mapper.update:
UPDATE CARTS
SET CART_QUANTITY = ${cartQuantity}
WHERE ITEM_CODE = #{itemCode} AND USER_CODE = #{userCode} AND USER_ID = #{userId}

mapper.delete:
DELETE
FROM CARTS
WHERE ITEM_CODE = #{itemCode} AND USER_CODE = #{userCode} AND USER_ID = #{userId}
